import { TOGGLE } from "../action/actionType";

const initialState = true;

export const showReducer = (state = { showCounter: initialState }, action) => {
  if (action.type === TOGGLE) {
    return {
      showCounter: !state.showCounter
    };
  } else {
    return state;
  }
};
